mapper.select:
select userid,password,phone,nickname,status,delflag,DATE_FORMAT(ctau.updatetime,'%Y-%m-%d %H:%i:%S') as updatetime,DATE_FORMAT(ctau.createtime,'%Y-%m-%d %H:%i:%S') as createtime
from  c_t_app_userinfo ctau

    and ctau.phone=#{phone}
    and ctau.nickname=#{nickname}
    and ctau.password=#{password}
    and ctau.delflag=0

select userid from c_t_app_userinfo 

	and invitecode=#{invitecode}

limit 1

mapper.update:
update c_t_app_userinfo 

   password = #{password},


	   and phone=#{phone}
	   and userid=#{userid}

mapper.select:
select ctau.userid, ctau.phone, ctau.password, ctau.nickname, ctau.status, ctau.invitecode, ctau.delflag, ctau.parentid, ctau.levelid, ctau.xgocoin, ctau.sumprofit, ctau.sumassets, ctau.growthvalue, DATE_FORMAT(ctau.updatetime,'%Y-%m-%d %H:%i:%S')  as updatetime, DATE_FORMAT(ctau.createtime,'%Y-%m-%d %H:%i:%S') as createtime 
from c_t_app_userinfo ctau 

	and ctau.phone=#{phone}
	and ctau.userid=#{userid}

limit 1
