mapper.update:
update c_t_app_userinfo 
set xgocoin = xgocoin + 1,
	growthvalue = growthvalue + #{growthvalue}
where userid = #{userid}

	update c_t_app_certification
	set delflag = 1
	
        and certificationid=#{certificationid}
         and userid = #{userid}
        and delflag=0

update c_t_app_userinfo
set isrealname = #{status}
where userid = #{userid}

mapper.select:
select userid,nickname,headimage,sum(grandfatherprofit) as sumprofit from (
	select us.userid,us.nickname,us.headimage,IFNULL(te.grandfatherprofit ,0) as grandfatherprofit
		from c_t_app_userinfo us
		inner join c_t_app_userinfo usp on us.parentid = usp.userid and usp.parentid = #{grandid}
		left join c_t_app_teamprofit te on te.userid = us.userid 
		where us.parentid  = #{userid}
	) A group by userid,nickname,headimage
	order by A.grandfatherprofit desc

mapper.update:
update c_t_app_userinfo 
set invitecodeqrcode = #{invitecodeqrcode}
where userid = #{userid}

mapper.select:
	select userid,password,phone,nickname,status,delflag,headimage,isrealname,
		case when ISNULL(paypassword) = 1 then 0 else 1 end as ispaypass,
    (select count(0) from c_t_app_paymentmethod where userid = #{userid}) ispaymentmethod
    from  c_t_app_userinfo 
    
    	and userid=#{userid}
    	and password=#{oldpassword}

mapper.update:
update c_t_app_userinfo
phone = #{phone}, paypassword = #{paypassword}, password = #{newpassword}, nickname = #{nickname}, headimage = #{headimage}, status = #{status}, delflag = #{delflag}, levelid = #{levelid}, isrealname = #{isrealname},
and userid=#{userid}

update c_t_app_paymentmethod
weixinnum = #{weixinnum}, weixinqrcode = #{weixinqrcode}, weixinrealname = #{weixinrealname}, alipaynum = #{alipaynum}, alipayqrcode = #{alipayqrcode}, alipayrealname = #{alipayrealname}, bank = #{bank}, banknum = #{banknum}, bankrealname = #{bankrealname},
and userid=#{userid} and paymentmethodid=#{paymentmethodid}
