mapper.update:
update c_t_app_order
status = #{status}, buytime = #{buytime}, payorder = #{payorder},
where orderid = #{orderid}

update c_t_app_order
set status = 3 
where orderid = #{orderid}

mapper.select:
select count(0) from c_t_app_order

	and status >= #{status} 
	and buyuserid = #{buyuserid} 
	and selluserid = #{selluserid}

mapper.update:
update c_t_app_userinfo
set growthvalue = growthvalue + #{growthvalue}
where userid = #{userid}

update c_t_app_userinfo
set sumprofit = sumprofit + #{profitprice}
where userid = #{userid}

update c_t_app_userinfo
set sumassets = sumassets + #{profitprice}
where userid = #{userid}

update c_t_app_userinfo 
set sumprofit = sumprofit + #{overprofit}
where userid = #{userid}

update c_t_app_rushtobuy
set status = #{status}
where userid = #{buyuserid}
and cardid = #{cardid}
and status = 1 
and createtime > CURDATE()

update c_t_app_order 
set rushtime = null, buyuserid = null
where orderid = #{orderid}

update c_t_app_userinfo 
set status = #{status}

	and userid = #{selluserid}
	and userid = #{buyuserid}

mapper.select:
select orderid,buyuserid,cardid from c_t_app_order

	and selluserid = #{selluserid}
	and buyuserid = #{buyuserid}
	and status = 1 and rushtime  <= date_add(now(), interval -1 hour)

select orderid from c_t_app_order

	and selluserid = #{selluserid}
	and buyuserid = #{buyuserid}
	and status = 2 and buytime  <= date_add(now(), interval -1 hour)
