mapper.select:
select userid,username,phone,email,realname,sex,birthday,headimage,status,createtime,updatetime,deletetime,delflag,address from c_t_manage_user

    and username=#{username}
    and password=#{password}
    and phone=#{phone}
    and userid=#{userid}

limit 1

select u.userid, u.phone, u.nickname, u.headimage, u.status,u.delflag,u.invitecode,u.invitecodehttpurl,u.invitecodeqrcode,u.parentid,u.levelid, u.xgocoin,u.sumprofit,u.sumassets,u.growthvalue,u.isrealname, u.createtime, u.updatetime from c_t_app_userinfo u left join c_t_app_certification c on u.userid = c.userid and c.status=1
and locate(#{phone},u.phone) > 0 and locate(#{nickname},u.nickname) > 0 and locate(#{realname},c.realname) > 0 and status = #{status} and u.delflag=0
order by u.createtime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_app_userinfo
status=#{status}, delflag=#{delflag}, updatetime=#{updatetime},
where userid=#{userid}

mapper.select:
select bannerid, imgurl, httpurl, type, status, updatetime, createtime 
from c_t_app_banner

	and type = #{type}
	and status = #{status}

order by createtime desc
limit #{startnum},#{rownum}

select  count(0) from c_t_app_banner

	and type = #{type}
	and status = #{status}

select bannerid, imgurl, httpurl, type, status, updatetime, createtime from c_t_app_banner
and bannerid = #{bannerid}
limit 1

mapper.update:
update c_t_app_banner
imgurl=#{imgurl}, httpurl=#{httpurl}, type=#{type}, updatetime=#{updatetime}, updateid=#{updateid}, status=#{status},
where bannerid=#{bannerid}

mapper.select:
    	select ao.orderid, ao.buyuserid, us1.nickname as buynickname, ao.selluserid, us2.nickname as sellnickname, mc.typename, ao.ordernum, ao.cardid,
			ao.type, ao.cardprice, ao.surpluswatchdays, ao.commentcount, ao.status, ao.ordertype, ao.payorder, ao.profitprice, ao.selltime, ao.buytime, ao.duetime, ao.rushtime, ao.createtime
		from c_t_app_order ao
		left join c_t_app_membercard mc on ao.cardid = mc.cardid
		left JOIN c_t_app_userinfo us1 on ao.buyuserid = us1.userid
		LEFT JOIN c_t_app_userinfo us2 on ao.selluserid = us2.userid
		
			and ao.ordernum = #{ordernum}
			and us1.nickname = #{buynickname}
			and us2.nickname = #{sellnickname}
			and mc.typename = #{typename}
			and ao.status = #{status}
			and ao.type = #{type}
			 ao.createtime >= #{ordertime} and ao.createtime <= DATE_ADD(#{ordertime},INTERVAL 1 DAY) 
		
		order by ao.createtime desc
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_order ao
		left join c_t_app_membercard mc on ao.cardid = mc.cardid
		left JOIN c_t_app_userinfo us1 on ao.buyuserid = us1.userid
		LEFT JOIN c_t_app_userinfo us2 on ao.selluserid = us2.userid
		
			and ao.ordernum = #{ordernum}
			and us1.nickname = #{buynickname}
			and us2.nickname = #{sellnickname}
			and mc.typename = #{typename}
			and ao.status = #{status}
			and ao.type = #{type}
			 ao.createtime >= #{ordertime} and ao.createtime <= DATE_ADD(#{ordertime},INTERVAL 1 DAY)

mapper.update:
update c_t_app_userinfo set status = #{status}
where userid in 
(select selluserid from c_t_app_order where orderid = #{orderid}
union select buyuserid from c_t_app_order where orderid = #{orderid})

mapper.select:
    	select am.movieid, am.coverimg , am.title, am.description, am.httpurl, am.type, am.status,
		am.createtime, am.updatetime
		from c_t_app_movie am
		
			and am.title = #{title}
			and am.description = #{description}
			and am.status = #{status}
		
		order by am.createtime desc
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_movie am
		
			and am.title = #{title}
			and am.description = #{description}
			and am.status = #{status}

mapper.update:
update c_t_app_movie
coverimg=#{coverimg}, title=#{title}, description=#{description}, httpurl=#{httpurl}, type=#{type}, updatetime=#{updatetime}, updateid=#{updateid}, status=#{status},
where movieid=#{movieid}

mapper.select:
    	select cardid,typename,forder,cardicon,cardimg,minprice,maxprice,xgocoin,starttime,endtime,commentstartdays,watchdays,commentcount,yield,introduction,description,createtime,updatetime,status
    	from c_t_app_membercard
    	
			and am.typename = #{typename}
			and am.status = #{status}
		
		order by forder
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_membercard
    	
			and am.typename = #{typename}
			and am.status = #{status}
