mapper.select:
select userid,username,phone,email,realname,sex,birthday,headimage,status,createtime,updatetime,deletetime,delflag,address from c_t_manage_user

    and username=#{username}
    and password=#{password}
    and phone=#{phone}
    and userid=#{userid}

limit 1

select u.userid, u.phone, u.nickname, u.headimage, u.status,u.delflag,u.invitecode,u.invitecodehttpurl,u.invitecodeqrcode,u.parentid,u.levelid, u.xgocoin,u.sumprofit,u.sumassets,u.growthvalue,u.isrealname, u.createtime, u.updatetime from c_t_app_userinfo u left join c_t_app_certification c on u.userid = c.userid and c.status=2
and locate(#{phone},u.phone) > 0 and locate(#{nickname},u.nickname) > 0 and locate(#{realname},c.realname) > 0 and u.status = #{status} and u.delflag=0
order by u.createtime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_app_userinfo
status=#{status}, delflag=#{delflag}, updatetime=#{updatetime},
where userid=#{userid}

mapper.select:
select bannerid, imgurl, httpurl, type, status, updatetime, createtime, description
from c_t_app_banner

	and type = #{type}
	and status = #{status}

order by createtime desc
limit #{startnum},#{rownum}

select  count(0) from c_t_app_banner

	and type = #{type}
	and status = #{status}

select bannerid, imgurl, httpurl, type, status, updatetime, createtime,description from c_t_app_banner
and bannerid = #{bannerid}
limit 1

mapper.update:
update c_t_app_banner
imgurl=#{imgurl}, httpurl=#{httpurl}, type=#{type}, updatetime=#{updatetime}, updateid=#{updateid}, status=#{status}, description=#{description},
where bannerid=#{bannerid}

mapper.select:
select advertid, advertcontent, imgurl, httpurl, createtime from c_t_app_advert
and locate(#{advertcontent}, advertcontent) > 0
order by createtime desc limit #{startnum},#{rownum}

select advertid, advertcontent, imgurl, httpurl, createtime from c_t_app_advert
and advertid = #{advertid}
limit 1

mapper.update:
update c_t_app_advert
advertcontent=#{advertcontent}, imgurl=#{imgurl}, httpurl=#{httpurl},
where advertid=#{advertid}

mapper.select:
select ao.orderid, ao.buyuserid, us1.nickname as buynickname, ao.selluserid, us2.nickname as sellnickname, mc.typename, ao.ordernum, ao.cardid, ao.type, ao.cardprice, ao.commentcount, ao.status, ao.ordertype, ao.payorder, ao.profitprice, ao.selltime, ao.buytime, ao.duetime, ao.rushtime, ao.createtime from c_t_app_order ao left join c_t_app_membercard mc on ao.cardid = mc.cardid left JOIN c_t_app_userinfo us1 on ao.buyuserid = us1.userid LEFT JOIN c_t_app_userinfo us2 on ao.selluserid = us2.userid
and locate(#{ordernum},ao.ordernum) > 0 and locate(#{buynickname},us1.nickname) > 0 and locate(#{sellnickname},us2.nickname) > 0 and locate(#{typename},mc.typename) > 0 and ao.status = #{status} and ao.type = #{type} ao.createtime >= #{ordertime} and ao.createtime <= DATE_ADD(#{ordertime},INTERVAL 1 DAY)
order by ao.createtime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_app_userinfo set status = #{status}
where userid in 
(select selluserid from c_t_app_order where orderid = #{orderid}
union select buyuserid from c_t_app_order where orderid = #{orderid})

mapper.select:
    	select am.movieid, am.coverimg , am.title, am.description, am.httpurl, am.type, am.status, am.hotforder,
		am.createtime, am.updatetime
		from c_t_app_movie am
		
			and locate(#{title},am.title) > 0
			and locate(#{description},am.description) > 0
			and am.status = #{status}
			and am.type = #{type}
		
		order by am.createtime desc
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_movie am
		
			and locate(#{title},am.title) > 0
			and locate(#{description},am.description) > 0
			and am.status = #{status}
			and am.type = #{type}

mapper.update:
update c_t_app_movie
coverimg=#{coverimg}, title=#{title}, description=#{description}, httpurl=#{httpurl}, type=#{type}, updatetime=#{updatetime}, updateid=#{updateid}, hotforder=#{hotforder}, status=#{status},
where movieid=#{movieid}

mapper.select:
    	select cardid,typename,forder,cardicon,cardimg,minprice,maxprice,xgocoin,starttime,endtime,commentstartdays,watchdays,commentcount,yield,introduction,description,createtime,updatetime,status
    	from c_t_app_membercard
    	
			and locate(#{typename},am.typename) > 0
			and am.status = #{status}
		
		order by forder
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_membercard
    	
			and locate(#{typename},am.typename) > 0
			and am.status = #{status}

mapper.update:
update c_t_app_membercard
typename=#{typename}, cardicon=#{cardicon}, cardimg=#{cardimg}, minprice=#{minprice}, maxprice=#{maxprice}, xgocoin=#{xgocoin}, starttime=#{starttime}, endtime=#{endtime}, commentstartdays=#{commentstartdays}, watchdays=#{watchdays}, commentcount=#{commentcount}, yield=#{yield}, introduction=#{introduction}, description=#{description}, status=#{status}, updatetime=#{updatetime}, updateid=#{updateid},
where cardid=#{cardid}

mapper.select:
select levelid,levelname,levelenum,mingrowthvalue,maxgrowthvalue,interesttimes, updatetime,updateid from c_t_app_memberlevel
and locate(#{levelname},levelname) > 0
order by levelenum limit #{startnum},#{rownum}

mapper.update:
update c_t_app_memberlevel
levelname=#{levelname}, mingrowthvalue=#{mingrowthvalue}, maxgrowthvalue=#{maxgrowthvalue}, interesttimes=#{interesttimes}, updatetime=#{updatetime}, updateid=#{updateid},
where levelid=#{levelid}

mapper.select:
select ap.paymentmethodid, au.nickname,ap.userid, ap.weixinnum, ap.weixinqrcode, ap.weixinrealname, ap.alipaynum, ap.alipayqrcode,ap.alipayrealname,ap.bank,ap.banknum,ap.bankrealname, ap.updatetime from c_t_app_paymentmethod ap left join c_t_app_userinfo au on ap.userid = au.userid
and (locate(#{realname},ap.weixinrealname) > 0 or locate(#{realname},ap.alipayrealname) > 0 or locate(#{realname},ap.bankrealname) > 0)
order by ap.updatetime desc limit #{startnum},#{rownum}

select ac.certificationid, ac.userid, ac.nationality, au.nickname, ac.realname, ac.idcard,ac.status,ac.message,ac.delflag, ac.updatetime from c_t_app_certification ac left join c_t_app_userinfo au on ac.userid = au.userid
and locate(#{realname},ac.realname) > 0 and locate(#{idcard},ac.idcard) > 0 and locate(#{nickname},au.nickname) > 0 and ac.delflag = 0
order by ac.updatetime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_app_userinfo
set isrealname = #{status}
where userid = (select userid from c_t_app_certification  where certificationid = #{certificationid})

update c_t_app_certification
status=#{status}, message=#{message}, updatetime=#{updatetime},
where certificationid=#{certificationid}

mapper.select:
select aq.questionid, aq.userid, aq.questioncontent, au.nickname, aq.systemuserid, aq.replycontent, aq.updatetime,aq.createtime from c_t_app_question aq left join c_t_app_userinfo au on aq.userid = au.userid
and locate(#{nickname},au.nickname) > 0 and qtype = #{qtype}
order by aq.updatetime desc limit #{startnum},#{rownum}

select noticeid, title, message,status,createtime from c_t_notice
and locate(#{message}, message) > 0 and locate(#{title}, title) > 0 and status = #{status} and createtime >= #{createtime} and createtime <= DATE_ADD(#{createtime},INTERVAL 1 DAY)
order by createtime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_app_question
systemuserid=#{systemuserid}, replycontent=#{replycontent}, updatetime=#{updatetime},
where questionid=#{questionid}

mapper.select:
select ae.exchangeid, ae.userid, ae.amount, au.nickname, ae.ordernum, ae.createtime from c_t_app_exchange ae left join c_t_app_userinfo au on ae.userid = au.userid
and locate(#{nickname},au.nickname) > 0
order by ae.createtime desc limit #{startnum},#{rownum}

    	select am.movieid, am.cardid, am.coverimg , am.title, am.description, am.httpurl, am.status,mc.typename,am.hotforder,
		am.createtime, am.updatetime
		from c_t_app_membermovie am
		left join c_t_app_membercard mc on am.cardid = mc.cardid
		
			and locate(#{title},am.title) > 0
			and locate(#{description},am.description) > 0
			and am.status = #{status}
			and am.cardid = #{cardid}
		
		order by am.createtime desc
		limit #{startnum},#{rownum}

    	select count(0) from c_t_app_membermovie am
		
			and locate(#{title},am.title) > 0
			and locate(#{description},am.description) > 0
			and am.status = #{status}
			and am.cardid = #{cardid}

mapper.update:
update c_t_app_membermovie
coverimg=#{coverimg}, title=#{title}, description=#{description}, httpurl=#{httpurl}, hotforder=#{hotforder}, updatetime=#{updatetime}, updateid=#{updateid}, status=#{status},
where movieid=#{movieid}

mapper.select:
select title, message,createtime from c_t_usernotice
and locate(#{title},title) > 0 and locate(#{message},message) > 0 and createtime >= #{createtime} and createtime <= DATE_ADD(#{createtime},INTERVAL 1 DAY) and type = 1
group by title,message,createtime order by createtime desc limit #{startnum},#{rownum}

mapper.update:
update c_t_rate
forder=#{forder}, minute=#{minute}, rate=#{rate}, lastflag=#{lastflag}, updatetime=#{updatetime},
where movieid=#{movieid}
