mapper.update:
update c_t_app_rushtobuy
set status = #{aftstatus}

	and cardid=#{cardid}
	and userid=#{userid}
	and status = #{befstatus}
	and TO_DAYS(createtime) <= TO_DAYS(NOW())

mapper.select:
select userid from c_t_app_rushtobuy
where cardid=#{cardid} 
and status = #{befstatus}
and TO_DAYS(createtime) <= TO_DAYS(NOW())

mapper.update:
update c_t_app_userinfo
set xgocoin =  xgocoin - #{xgocoin}
where userid = #{userid}

mapper.select:
select userid from c_t_app_userinfo 
where isadmin = 1 and status = 0 and delflag = 0
and userid != #{userid}
